import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { FaSignOutAlt } from 'react-icons/fa';

export default function Navbar() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const history = useHistory();

  useEffect(() => {
    // Check if token exists in localStorage
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true); // User is authenticated
    }
  }, []);

  const handleSignOut = () => {
    // Remove token and donorId from localStorage
    localStorage.removeItem('token');
    localStorage.removeItem('donorId');
    setIsAuthenticated(false); // Update authentication status
    history.push('/signin'); // Redirect to sign-in page
  };

  return (
    <div className="navbar-main">
      <div className="container">
        <div className="navbar-header">
          <button
            type="button"
            className="navbar-toggle collapsed"
            data-toggle="collapse"
            data-target="#navbar"
            aria-expanded="false"
            aria-controls="navbar"
          >
            <span className="sr-only">Toggle navigation</span>
            <span className="icon-bar"></span>
            <span className="icon-bar"></span>
            <span className="icon-bar"></span>
          </button>

          <a className="navbar-brand" href="/">
            <img src="assets/images/sadaka-logo.png" alt="" />
          </a>
        </div>

        <div id="navbar" className="navbar-collapse collapse pull-right">
          <ul className="nav navbar-nav">
            <li>
              <a className="is-active" href="/">
                HOME
              </a>
            </li>
            <li>
              <a href="about.html">ABOUT</a>
            </li>
            <li>
              <a href="gallery.html">GALLERY</a>
            </li>
            <li>
              <a href="contact.html">CONTACT</a>
            </li>
            {isAuthenticated ? (
              <>
                <li>
                  <Link to="/my-donations" className="btn btn-primary">
                    YOUR DONATIONS
                  </Link>
                </li>
                <li>
                  <button onClick={handleSignOut} className="btn btn-link signout-button">
                    <FaSignOutAlt /> Sign Out
                  </button>
                </li>
              </>
            ) : (
              <li>
                <Link to="/signin" className="btn btn-primary">
                  SIGN IN
                </Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
}
